using System.Text;

class AsciiArt
{
    private static void PrintAsciiArt(int width,  int height, string[] chars, string stringToConvert)
    {
        //value of a
        var val_a = Encoding.ASCII.GetBytes("a")[0];
        //value of A
        var val_A = Encoding.ASCII.GetBytes("A")[0];

        var tBytes = Encoding.ASCII.GetBytes(stringToConvert);

        //convert the string to its integer value : 0 for a/A, 1 for b/B... and 26 for others
        for (int i = 0; i < stringToConvert.Length; i++)
        {
            var val = tBytes[i];
            //convert lower case character
            if (val_a <= val && val <= val_a + 25)
            {
                tBytes[i] -= val_a;
            }
            //convert upper case character
            else if (val_A <= val && val <= val_A + 25)
            {
                tBytes[i] -= val_A;
            }
            //other characters
            else
            {
                tBytes[i] = 26;
            }
        }

        for (int h = 0; h < height; h++)
        {
            for (int i = 0; i < stringToConvert.Length; i++)
            {
                Console.Write(chars[h].Substring(tBytes[i] * width, width));
            }
            Console.WriteLine();
        }
    }

